#[derive(Debug)]
pub struct AudDMock;

#[async_trait(?Send)]
impl TestProvider for AudDMock {
    async fn recognize_impl(&self, _: &[u8], mode: TestProviderMode) -> Result<Song> {
        Ok(AudD::build_song_from_data(random_data(mode)?))
    }
}

